import Card from "../Card";
interface IForms {
  code: number;
  category: string;
  name: string;
  provider: string;
  value: number;
  id: number;
}

function Form() {
  const {
    data,
    setData,
    setCategory,
    setCode,
    setName,
    setProvider,
    setValue,
    name,
    value,
    category,
    code,
  } = useForms();

  return (
    <>
      <Div>
        <Input
          placeholder="Nome"
          onChange={(e) => setName(e.target.value)}
          value={name}
        />

