import { createContext, useContext, useState } from "react";
import { ReactNode } from "react";

interface IForms {
  children: ReactNode;
}

interface IFormsProps {
  handleSubmit: () => void;
  removeProduct: (id: number) => void;
  editProduct: (id: number) => void;
  code: number;
  category: string;
  name: string;
  provider: string;
  value: number;
  id: number;
  data: Object;
}

interface IFormsContext {
  code: number;
  category: string;
  name: string;
  provider: string;
  value: number;
  id: number;
}

const FormsContext = createContext({} as IFormsProps);
export const FormsProvider = ({ children }: IForms) => {
  const [name, setName] = useState<string>("");
  const [code, setCode] = useState<number>(0);
  const [category, setCategory] = useState<string>("");
  const [provider, setProvider] = useState<string>("");
  const [value, setValue] = useState<number>(0);
  const [data, setData] = useState<IFormsContext[]>([] as IFormsContext[]);
  const [msg, setMsg] = useState<boolean>(false);
  const [id, setId] = useState<number>(0);
  const [authToken, setAuthToken] = useState(
    () => localStorage.getItem("token") || ""
  );

  const handleSubmit = () => {
    const newProduct = { name, category, code, provider, value };
    const checkInputs = Object.values(newProduct).every(
      (item) => item.toString().length
    );
    const newId = data.reduce(
      (acc, item) => (item.id >= acc ? item.id + 1 : acc),
      1
    );
    setData([...data, { ...newProduct, id: newId }]);
  };

  const removeProduct = (id: number) => {
    const filtered = data.filter((item) => item.id !== id);
    setData(filtered);
  };

  const editProduct = (id: number) => {
    const updatedList = data.map((item, index) => {
      if (id !== index) return item;
      return { ...item, complete: !item.name };
    });
    setData(updatedList);
  };

  return (
    <FormsContext.Provider
      value={{
        removeProduct,
        handleSubmit,
        editProduct,
        data,
        id,
        value,
        name,
        code,
        category,
        provider,
      }}
    >
      {children}
    </FormsContext.Provider>
  );
};

export const useForms = () => useContext(FormsContext);

export default FormsProvider;
