import { useForms } from "../../Providers/Forms";
import { useState } from "react";

interface IForms {
  code: number;
  category: string;
  name: string;
  provider: string;
  value: number;
  id: number;
  data: Object;
}

function Form() {
  const { handleSubmit, removeProduct } = useForms();

  const [name, setName] = useState<string>("");
  const [code, setCode] = useState<number>(0);
  const [category, setCategory] = useState<string>("");
  const [provider, setProvider] = useState<string>("");
  const [value, setValue] = useState<number>(0);
  const [data, setData] = useState<IForms[]>([] as IForms[]);

  return (
    <div>
      <form onChange={() => setData}>
        <input
          placeholder="*Nome"
          onChange={(e) => setName(e.target.value)}
          value={name}
        ></input>
        <input
          type="number"
          placeholder="*CÃ³digo"
          onChange={(e) => setCode(Number(e.target.value))}
          value={code}
        ></input>
        <input
          placeholder="*Categoria"
          onChange={(e) => setCategory(e.target.value)}
          value={category}
        ></input>
        <input
          placeholder="*Provedor"
          onChange={(e) => setProvider(e.target.value)}
          value={provider}
        ></input>
        <input
          placeholder="*Valor"
          onChange={(e) => setValue(Number(e.target.value))}
          value={value}
        ></input>

        <div>
          <button onClick={(data) => handleSubmit}>Adicionar</button>
        </div>
      </form>

      <div className="card__container">
        {data.map(({ id, name, value, provider, code }, index) => {
          return (
            <div>
              <h3>{name}</h3>
              <p>{value}</p>
              <p> {provider}</p>
              <p> {code}</p>
              <button onClick={() => removeProduct(id)}>Remover</button>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Form;
