import { useForms } from "../../Providers/Forms";
import { useEffect, useState } from "react";

interface IForms {
  code: number;
  category: string;
  name: string;
  provider: string;
  value: number;
  id: number;
}

function Form() {
  const { removeProduct, data, editProduct, setData, id } = useForms();

  useEffect(() => {
    editProduct(id);
  }, [data]);

  const handleSubmit = () => {
    const newProduct = { name, category, code, provider, value };
    const newId = data.reduce(
      (acc, item) => (item.id >= acc ? item.id + 1 : acc),
      1
    );
    setData([...data, { ...newProduct, id: newId }]);

    localStorage.setItem("products", JSON.stringify(newProduct));
  };

  const [name, setName] = useState<string>("");
  const [code, setCode] = useState<number>(0);
  const [category, setCategory] = useState<string>("");
  const [provider, setProvider] = useState<string>("");
  const [value, setValue] = useState<number>(0);

  return (
    <div>
      <div>
        <input
          placeholder="*Nome"
          onChange={(e) => setName(e.target.value)}
          value={name}
        ></input>
        <input
          type="number"
          placeholder="*Código"
          onChange={(e) => setCode(Number(e.target.value))}
          value={code}
        ></input>
        <input
          placeholder="*Categoria"
          onChange={(e) => setCategory(e.target.value)}
          value={category}
        ></input>
        <input
          placeholder="*Provedor"
          onChange={(e) => setProvider(e.target.value)}
          value={provider}
        ></input>
        <input
          placeholder="*Valor"
          onChange={(e) => setValue(Number(e.target.value))}
          value={value}
        ></input>

        <div>
          <button onClick={handleSubmit}>Adicionar</button>
        </div>
      </div>

      <div className="card__container">
        {data.map(({ id, name, value, provider, code }, index) => {
          return (
            <div>
              <h3>{name}</h3>
              <p>{value}</p>
              <p> {provider}</p>
              <p> {code}</p>
              <button onClick={() => removeProduct(id)}>Remover</button>
              <div>
                <input
                  placeholder="*Nome"
                  onChange={(e) => setName(e.target.value)}
                  value={name}
                ></input>
                <input
                  type="number"
                  placeholder="*Código"
                  onChange={(e) => setCode(Number(e.target.value))}
                  value={code}
                ></input>
                <input
                  placeholder="*Categoria"
                  onChange={(e) => setCategory(e.target.value)}
                  value={category}
                ></input>
                <input
                  placeholder="*Provedor"
                  onChange={(e) => setProvider(e.target.value)}
                  value={provider}
                ></input>
                <input
                  placeholder="*Valor"
                  onChange={(e) => setValue(Number(e.target.value))}
                  value={value}
                ></input>
              </div>
              <button onClick={() => editProduct(id)}>Editar</button>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Form;
