import { useForms } from "../../Providers/Forms";
import { useEffect, useState } from "react";
import { Input, Div, Button } from "./style";
import Card from "../Card";
interface IForms {
  code: number;
  category: string;
  name: string;
  provider: string;
  value: number;
  id: number;
}

function Form() {
  const { removeProduct, data, editProduct, setData, id, editar } = useForms();

  const handleSubmit = () => {
    const newProduct = { name, category, code, provider, value };
    const newId = data.reduce(
      (acc, item) => (item.id >= acc ? item.id + 1 : acc),
      1
    );
    setData([...data, { ...newProduct, id: newId }]);

    localStorage.setItem("products", JSON.stringify(newProduct));
  };

  const [name, setName] = useState<string>("");
  const [code, setCode] = useState<number>(0);
  const [category, setCategory] = useState<string>("");
  const [provider, setProvider] = useState<string>("");
  const [value, setValue] = useState<number>(0);

  return (
    <>
      <Div>
        <Input
          placeholder="Nome"
          onChange={(e) => setName(e.target.value)}
          value={name}
        />

        <Input
          type="number"
          placeholder="CÃ³digo"
          onChange={(e) => setCode(Number(e.target.value))}
          value={code}
        />
        <Input
          placeholder="Categoria"
          onChange={(e) => setCategory(e.target.value)}
          value={category}
        />
        <Input
          placeholder="Provedor"
          onChange={(e) => setProvider(e.target.value)}
          value={provider}
        />
        <Input
          placeholder="Valor"
          onChange={(e) => setValue(Number(e.target.value))}
          value={value}
        />

        <Button onClick={handleSubmit}>Adicionar</Button>
      </Div>

      {data.map(({ id, name, value, provider, code, category }, index) => {
        return (
          <Card
            name={name}
            value={value}
            provider={provider}
            code={code}
            category={category}
            id={id}
          />
        );
      })}
    </>
  );
}

export default Form;
