import { useForms } from "../../Providers/Forms";
import { useEffect, useState } from "react";
import { Input, Div, Button } from "../Form/style";

interface SimpleDialogProps {
  code: number;
  category: string;
  name: string;
  provider: string;
  value: number;
  id: number;
  open: boolean;
  onClose: (value: string) => void;
}
function EditProduct(props: SimpleDialogProps) {
  const {
    data,
    setCategory,
    setCode,
    setName,
    setProvider,
    setValue,
    name,
    value,
    category,
    code,
    handleSubmit,
    provider,
    editProduct,
  } = useForms();

  const { onClose, selectedValue, open } = props;
  return (
    <Div>
      <Input
        placeholder="Nome"
        onChange={(e) => setName(e.target.value)}
        value={name}
      />

      <Input
        type="number"
        placeholder="CÃ³digo"
        onChange={(e) => setCode(Number(e.target.value))}
        value={code}
      />
      <Input
        placeholder="Categoria"
        onChange={(e) => setCategory(e.target.value)}
        value={category}
      />
      <Input
        placeholder="Provedor"
        onChange={(e) => setProvider(e.target.value)}
        value={provider}
      />
      <Input
        placeholder="Valor"
        onChange={(e) => setValue(Number(e.target.value))}
        value={value}
      />

      <Button onClick={() => editProduct}>Adicionar</Button>
    </Div>
  );
}

export default EditProduct;
