import styled from "styled-components";

export const ModalUp = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  position: absolute;
  width: 75%;
  top: 20%;
  left: 12%;

  background-color: #788f85;

  form {
    max-width: 400px;
    min-width: 290px;
    width: 55%;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    padding: 5px;
    gap: 20px;

    .account {
      width: 60%;
    }

    h2 {
      color: var(--white);
      margin-top: 20px;
      font-weight: 600;
    }

    input {
      color: var(--white);
      &::placeholder {
        color: var(--white);
      }
    }

    span {
      color: var(--yellow);
    }
  }

  @media only screen and (min-width: 1024px) {
    width: 50%;
    height: 50vh;
    top: 30%;
    left: 25%;

    form {
      width: 35%;
      display: flex;
      justify-content: center;
      align-items: center;
      flex-direction: column;
      gap: 20px;

      button {
        opacity: 1;
        color: var(--white);
        background-color: var(--orangeCore);
      }
    }
  }
`;
