import { useState } from "react";
import { useHistory, Link } from "react-router-dom";
import {
  ContainerUser,
  HeaderUser,
  AvatarUser,
  Container,
  BtnAvatar,
  DataUser,
  BtnProfile,
} from "./styles";
import arrowLeft from "../../assets/arrowLeft.svg";
import Modal from "react-modal";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    backgoundColor: "blue",
    width: "80%",
  },
};

const UserProfile = () => {
  const [modalData, setModalData] = useState(false);
  const history = useHistory();

  return (
    <ContainerUser>
      <HeaderUser />
      <Container>
        <Link to="/user-profile">
          <img src={arrowLeft} alt="voltar" />
        </Link>
        <BtnAvatar>Foto do perfil</BtnAvatar>
      </Container>

      <AvatarUser>
        <img src="https://i.pravatar.cc/150" alt="perfil" />
      </AvatarUser>

      <BtnProfile onClick={() => setModalData(true)}>Editar</BtnProfile>

      <DataUser>
        <p>Nome: </p>
        <p>E-mail: </p>
        <p>Celular: </p>
      </DataUser>
    </ContainerUser>
  );
};

export default UserProfile;
