import { useHistory } from "react-router";
import { HeaderContainer, MenuMobile, MenuDesk } from "./styles";
import { FiAlignJustify, FiX } from "react-icons/fi";
import { useState } from "react";

export const Header = () => {
  const history = useHistory();
  const [menuDrop, setMenuDrop] = useState(false);

  const toSend = (path: string) => {
    return history.push(path);
  };

  const OpenMobile = () => {
    setMenuDrop(true);
  };
  const CloseMobile = () => {
    setMenuDrop(false);
  };

  return (
    <>
      <HeaderContainer>
        <h1>Logistc Tech</h1>
        {menuDrop ? (
          <FiX onClick={CloseMobile} />
        ) : (
          <FiAlignJustify onClick={OpenMobile} />
        )}

        {menuDrop ? (
          <MenuMobile>
            <li>
              <button onClick={() => toSend("/dashboard")}> Cadastro</button>
            </li>
            <li>
              <button onClick={() => toSend("/storebd")}> Produtos</button>
            </li>
            <li>
              <button onClick={() => toSend("/")}>Sair</button>
            </li>
          </MenuMobile>
        ) : (
          <></>
        )}

        <MenuDesk>
          <ul>
            <li>
              <button onClick={() => toSend("/dashboard")}> Cadastro</button>
            </li>
            <li>
              <button onClick={() => toSend("/storebd")}> Produtos</button>
            </li>
          </ul>
        </MenuDesk>
      </HeaderContainer>
    </>
  );
};
