import { ContainerBD, CardProducts } from "./styles";
import { Header } from "../../components/Header";
import { useProducts } from "../../providers/Products";
import { ModalUpdate } from "../../components/ModalUpdate";

export const StoreBD = () => {
  const { store, setOpenModal, openModal, setCodeProduct } = useProducts();

  const openModalUpdate = (productId: string) => {
    localStorage.setItem("productId", productId);
    setCodeProduct(productId);
    setOpenModal(true);
  };

  return (
    <>
      <Header />
      {openModal ? <ModalUpdate /> : <></>}
      <ContainerBD>
        {store &&
          store.map((item, index) => (
            <CardProducts key={item.productId}>
              <span>{item.name}</span>
              <span>{item.category}</span>
              <span>{item.price} R$</span>
              <span>{item.provider}</span>
              <span>{item.productId}</span>
              <button onClick={() => openModalUpdate(item.productId)}>
                Atualizar
              </button>
            </CardProducts>
          ))}
      </ContainerBD>
    </>
  );
};
