import { ContainerBD, CardProducts } from "./styles";
import { Header } from "../../components/Header";
import { useProducts } from "../../providers/Products";
import { ModalUpdate } from "../../components/ModalUpdate";
import { useEffect } from "react";
import axios from "axios";
import { useAuthLogin } from "../../providers/Auth";

export const StoreBD = () => {
  const { store, setOpenModal, openModal, setCodeProduct, setStore } =
    useProducts();
  console.log(store);
  const { token } = useAuthLogin();

  const openModalUpdate = (productId: number) => {
    console.log(productId, "storebd");
    setCodeProduct(productId);
    setOpenModal(true);
  };

  useEffect(() => {
    axios
      .get("https://pierrekalil1.herokuapp.com/products", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((resp) => {
        setStore(resp.data);
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <>
      <Header />
      {openModal ? <ModalUpdate /> : <></>}
      <ContainerBD>
        {store &&
          store.map((item, index) => (
            <CardProducts key={index}>
              <span>{item.name}</span>
              <span>{item.category}</span>
              <span>{item.price} R$</span>
              <span>{item.provider}</span>
              <span>{item.id}</span>
              <button onClick={() => openModalUpdate(item.id)}>
                Atualizar
              </button>
            </CardProducts>
          ))}
      </ContainerBD>
    </>
  );
};
