import styled from "styled-components";

export const HeaderContainer = styled.div`
  display: flex;
  justify-content: space-around;
  align-items: center;
  height: 10vh;
  width: 100%;

  h1 {
    padding: 20px;
    color: var(--white);
  }

  ul {
    display: flex;
  }
`;
