import styled from "styled-components";

export const ModalUp = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  position: relative;
  width: 100%;
  height: 100vh;
  z-index: 999;

  background-color: transparent;

  form {
    max-width: 400px;
    min-width: 290px;
    width: 55%;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    gap: 20px;
    .account {
      width: 60%;
    }

    h2 {
      color: var(--white);
      margin-top: 20px;
      font-weight: 600;
    }

    input {
      color: var(--white);
      &::placeholder {
        color: var(--white);
      }
    }

    span {
      color: var(--yellow);
    }
  }

  @media only screen and (min-width: 1024px) {
    width: 50%;
    height: 60vh;

    form {
      width: 45%;
      display: flex;
      justify-content: center;
      align-items: center;
      flex-direction: column;
      gap: 20px;

      button {
        opacity: 1;
        color: var(--white);
        background-color: var(--orangeCore);
      }
    }
  }
`;
