import { ModalUp } from "./styles";
import { Input } from "../Input";
import { yupResolver } from "@hookform/resolvers/yup";
import { useForm } from "react-hook-form";
import * as yup from "yup";
import { useProducts } from "../../providers/Products";
import { UpdateProductsData } from "../../types/UpdateProductsData";

export const ModalUpdate = () => {
  const { updateProducts, setOpenModal, codeProduct } = useProducts();

  const schema = yup.object().shape({
    name: yup.string().required("Confirme o nome"),
    provider: yup.string().required(),
    price: yup.number().required(),
    category: yup.string().required(),
  });

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  const onSubmitFunction = (data: UpdateProductsData) => {
    updateProducts(data, codeProduct);
    setOpenModal(false);
    reset();
  };
  return (
    <>
      <ModalUp>
        <form onSubmit={handleSubmit(onSubmitFunction)}>
          <h2>Atualizar dados</h2>
          <Input
            type="text"
            placeholder="Nome"
            register={register}
            name="name"
            error={errors.name?.message}
          />
          <Input
            type="text"
            placeholder="Categoria"
            register={register}
            name="category"
            error={errors.category?.message}
          />
          <Input
            type="number"
            placeholder="PreÃ§o"
            register={register}
            name="price"
            error={errors.price?.message}
          />
          <Input
            type="text"
            placeholder="Fornecedor"
            register={register}
            name="provider"
            error={errors.provider?.message}
          />
          <button type="submit">Salvar</button>
        </form>
      </ModalUp>
    </>
  );
};
