import styled from "styled-components";

export const HeaderContainer = styled.div`
  display: flex;
  justify-content: space-around;
  align-items: center;
  height: 10vh;
  width: 100%;
  border-bottom: var(--white) solid 1px;

  h1 {
    padding: 20px;
    color: var(--white);
  }

  ul {
    display: flex;
  }

  li {
    padding: 0 15px;
    color: var(--white);
  }

  button {
    padding: 10px;
    border: none;
    border-radius: 20px;
    background-color: transparent;
    color: var(--white);
    font-weight: bold;
  }

  button:hover {
    background-color: var(--white);
    color: var(--primary);
    transition: all 0.5s;
  }
`;
