import styled from "styled-components";

export const ContainerHeader = styled.div`
  /* margin-top: 50px; */
  width: 100%;
  height: 25vh;
  border-style: solid;
  border-color: var(--greenFormBkgd) #fff #fff var(--greenFormBkgd);
  border-width: 100px 0px 80px 320px;

  @media (min-width: 425px) {
    border-width: 100px 0px 80px 450px;
  }

  @media (min-width: 768px) {
    border-width: 200px 0px 80px 700px;
  }
`;
