import { yupResolver } from "@hookform/resolvers/yup";
import { useForm } from "react-hook-form";
import * as yup from "yup";
import { FormContainer } from "./styles";
import { Input } from "../Input";
import { ProductsData } from "../../types/ProductsData";
import { useProducts } from "../../providers/Products";

export const FormProducts = () => {
  const { registerProducts } = useProducts();
  const schema = yup.object().shape({
    name: yup.string().required("Campo obrigatório"),
    provider: yup.string().required("Campo obrigatório"),
    price: yup
      .number()
      .typeError("Campo obrigatório")
      .required("Campo obrigatório")
      .min(1, "Valor mínimo 1,00 R$"),
    category: yup.string().required("Campo obrigatório"),
  });

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  const onSubmitFunction = (data: ProductsData) => {
    registerProducts(data);
    reset();
  };

  return (
    <FormContainer>
      <form onSubmit={handleSubmit(onSubmitFunction)}>
        <h2>Cadastro de Produtos</h2>
        <Input
          type="text"
          placeholder="Nome"
          register={register}
          name="name"
          error={errors.name?.message}
        />
        <Input
          type="text"
          placeholder="Categoria"
          register={register}
          name="category"
          error={errors.category?.message}
        />
        <Input
          type="number"
          placeholder="Preço"
          register={register}
          name="price"
          error={errors.price?.message}
        />
        <Input
          type="text"
          placeholder="Fornecedor"
          register={register}
          name="provider"
          error={errors.provider?.message}
        />
        <button type="submit">Cadastrar</button>
      </form>
    </FormContainer>
  );
};
