import styled from "styled-components";

export const HeaderContainer = styled.div`
  display: flex;
  justify-content: space-around;
  align-items: center;
  height: 10vh;
  width: 100%;
  border-bottom: #3ecd7b solid 0.5px;

  h1 {
    padding: 20px;
    color: var(--white);
  }

  ul {
    display: flex;
  }

  li {
    padding: 0 15px;
    color: var(--white);
  }

  button {
    padding: 10px;
    border: none;
    border-radius: 20px;
    background-color: transparent;
    color: var(--white);
    font-weight: bold;
  }

  button:hover {
    background-color: var(--secondary);
    color: var(--primary);
    transition: all 0.5s;
  }

  svg {
    width: 30px;
    height: 30px;
    color: var(--white);
  }
`;

export const MenuMobile = styled.div`
  display: flex;

  @media only screen and (max-width: 599px) {
    display: none;
  }
`;

export const MenuDesk = styled.div`
  display: none;

  @media only screen and (min-width: 600px) {
    display: flex;
  }
`;
