  height: 25vh;
  border-style: solid;
  border-color: var(--greenFormBkgd) #fff #fff var(--greenFormBkgd);
  border-width: 100px 0px 80px 320px;

  @media (min-width: 425px) {
    border-width: 100px 0px 80px 450px;
  }

  @media (min-width: 768px) {
    border-width: 200px 0px 80px 800px;
  }

  @media (min-width: 1024px) {
    border-width: 200px 0px 80px 1000px;
  }

  @media (min-width: 1440px) {
    border-width: 200px 0px 150px 1500px;
  }
`;

export const User = styled.img`
  /* width: 200px;
  height: 200px; */
